// Função para formatar a data
function formatarData(data) {
    let dia = data.getDate();
    let mes = data.getMonth() + 1; // Os meses são indexados de 0 a 11
    let ano = data.getFullYear();

    // Adiciona zero à frente do dia e mês se forem menores que 10
    if (dia < 10) {
        dia = '0' + dia;
    }

    if (mes < 10) {
        mes = '0' + mes;
    }

    return `${ano}-${mes}-${dia}`;
}

